import "core-js/modules/es.array.push.js";
import { auth } from "@/firebase"; // Ajusta la ruta según tu proyecto
import { signInWithEmailAndPassword } from "firebase/auth";
export default {
  data() {
    return {
      email: "",
      password: "",
      valid: false,
      errorMessage: "",
      emailRules: [v => !!v || "El correo es obligatorio", v => /.+@.+\..+/.test(v) || "El correo debe ser válido"],
      passwordRules: [v => !!v || "La contraseña es obligatoria", v => v.length >= 6 || "La contraseña debe tener al menos 6 caracteres"]
    };
  },
  methods: {
    async login() {
      this.errorMessage = "";
      const form = this.$refs.form;
      if (form.validate()) {
        try {
          await signInWithEmailAndPassword(auth, this.email, this.password);
          this.$router.push("/dashboard");
        } catch (error) {
          this.errorMessage = "Error al iniciar sesión: " + error.message;
        }
      }
    },
    forgotPassword() {
      // Aquí puedes agregar la lógica para recuperar la contraseña
      alert("Función para recuperar contraseña aquí.");
    },
    goToSignup() {
      this.$router.push("/signup");
    }
  }
};
